import React, { useState, useEffect } from 'react';
import { fetchLatest } from './api';
import SensorCard from './components/SensorCard';
import Graph from './components/Graph';
import './App.css';

function App() {
    const [latest, setLatest] = useState({});
    const [history, setHistory] = useState([]);

    useEffect(() => {
        const updateData = async () => {
            const data = await fetchLatest();
            setLatest(data);
            setHistory(prev => [...prev, data].slice(-20)); // garder 20 points max
        };

        updateData();
        const interval = setInterval(updateData, 5000);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="App" style={{ padding: '2rem' }}>
            <h1>IoT Dashboard</h1>

            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                <SensorCard label="Temperature" value={latest.temperature} unit="°C" />
                <SensorCard label="Humidity" value={latest.humidity} unit="%" />
                <SensorCard label="CO2" value={latest.co2} unit="ppm" />
                <SensorCard label="Light" value={latest.light} unit="lux" />
                <SensorCard label="Noise" value={latest.noise} unit="dB" />
                <SensorCard label="Pressure" value={latest.pressure} unit="hPa" />
                <SensorCard label="PM2.5" value={latest.pm25} unit="µg/m³" />
            </div>

            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                <Graph data={history} dataKey="temperature" label="Temperature (°C)" />
                <Graph data={history} dataKey="humidity" label="Humidity (%)" />
                <Graph data={history} dataKey="co2" label="CO₂ (ppm)" />
                <Graph data={history} dataKey="light" label="Light (lux)" />
                <Graph data={history} dataKey="noise" label="Noise (dB)" />
                <Graph data={history} dataKey="pressure" label="Pressure (hPa)" />
                <Graph data={history} dataKey="pm25" label="PM2.5 (µg/m³)" />
            </div>
        </div>
    );
}

export default App;
