import React, { useState, useEffect } from 'react';
import { fetchLatest } from './api';
import SensorCard from './components/SensorCard';
import Graph from './components/Graph';
import './App.css';

function App() {
    const [latest, setLatest] = useState({});
    const [history, setHistory] = useState([]);

    useEffect(() => {
        const updateData = async () => {
            const data = await fetchLatest();
            setLatest(data);
            setHistory(prev => [...prev, data].slice(-20));
        };

        updateData();
        const interval = setInterval(updateData, 5000);
        return () => clearInterval(interval);
    }, []);

    const sensors = [
        { key: 'temperature', label: 'Temperature', value: latest.temperature, unit: '¬∞C', color: '#FF6B6B', icon: 'üå°Ô∏è' },
        { key: 'humidity', label: 'Humidity', value: latest.humidity, unit: '%', color: '#4ECDC4', icon: 'üíß' },
        { key: 'co2', label: 'CO‚ÇÇ', value: latest.co2, unit: 'ppm', color: '#95E1D3', icon: 'üí®' },
        { key: 'light', label: 'Light', value: latest.light, unit: 'lux', color: '#FFE66D', icon: '‚òÄÔ∏è' },
        { key: 'noise', label: 'Noise', value: latest.noise, unit: 'dB', color: '#A8E6CF', icon: 'üîä' },
        { key: 'pressure', label: 'Pressure', value: latest.pressure, unit: 'hPa', color: '#C7CEEA', icon: 'üìä' },
        { key: 'pm25', label: 'PM2.5', value: latest.pm25, unit: '¬µg/m¬≥', color: '#B4A7D6', icon: '‚òÅÔ∏è' }
    ];

    return (
        <div style={{
            minHeight: '100vh',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)',
            padding: '2rem',
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
        }}>
            <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
                <header style={{ marginBottom: '2rem', textAlign: 'center' }}>
                    <h1 style={{ 
                        color: '#fff', 
                        fontSize: '3rem',
                        fontWeight: '800',
                        marginBottom: '0.5rem',
                        textShadow: '0 4px 20px rgba(0,0,0,0.3)',
                        letterSpacing: '-1px'
                    }}>
                        IoT Dashboard
                    </h1>
                    <p style={{ 
                        color: 'rgba(255,255,255,0.8)', 
                        fontSize: '1.1rem',
                        fontWeight: '400'
                    }}>
                        Real-time environmental monitoring
                    </p>
                </header>

                <div style={{ 
                    display: 'flex', 
                    flexWrap: 'wrap',
                    gap: '1rem',
                    marginBottom: '2rem'
                }}>
                    {sensors.map(sensor => (
                        <div
                            key={sensor.key}
                            style={{
                                background: 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
                                backdropFilter: 'blur(10px)',
                                border: '1px solid rgba(255,255,255,0.18)',
                                borderRadius: '20px',
                                padding: '1.5rem',
                                minWidth: '200px',
                                flex: '1 1 200px',
                                transition: 'all 0.3s ease',
                                cursor: 'pointer',
                                position: 'relative',
                                overflow: 'hidden'
                            }}
                            onMouseEnter={(e) => {
                                e.currentTarget.style.transform = 'translateY(-8px)';
                                e.currentTarget.style.boxShadow = `0 20px 40px ${sensor.color}33`;
                                e.currentTarget.style.borderColor = sensor.color;
                            }}
                            onMouseLeave={(e) => {
                                e.currentTarget.style.transform = 'translateY(0)';
                                e.currentTarget.style.boxShadow = '0 8px 32px rgba(0,0,0,0.1)';
                                e.currentTarget.style.borderColor = 'rgba(255,255,255,0.18)';
                            }}
                        >
                            <div style={{
                                position: 'absolute',
                                top: '-50%',
                                right: '-20%',
                                width: '150px',
                                height: '150px',
                                background: `radial-gradient(circle, ${sensor.color}22 0%, transparent 70%)`,
                                borderRadius: '50%',
                                pointerEvents: 'none'
                            }} />

                            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '1rem', position: 'relative' }}>
                                <div style={{
                                    background: `${sensor.color}22`,
                                    borderRadius: '12px',
                                    padding: '0.6rem',
                                    marginRight: '0.8rem',
                                    fontSize: '1.5rem'
                                }}>
                                    {sensor.icon}
                                </div>
                                <span style={{ 
                                    color: 'rgba(255,255,255,0.7)', 
                                    fontSize: '0.9rem',
                                    fontWeight: '500',
                                    letterSpacing: '0.5px'
                                }}>{sensor.label}</span>
                            </div>

                            <div style={{ position: 'relative' }}>
                                <span style={{ 
                                    fontSize: '2.5rem', 
                                    fontWeight: '700',
                                    color: '#fff',
                                    lineHeight: '1',
                                    textShadow: `0 0 20px ${sensor.color}66`
                                }}>
                                    {sensor.value || '--'}
                                </span>
                                <span style={{ 
                                    fontSize: '1.1rem', 
                                    color: sensor.color,
                                    marginLeft: '0.5rem',
                                    fontWeight: '600'
                                }}>
                                    {sensor.unit}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>

                <div style={{ 
                    display: 'flex', 
                    flexWrap: 'wrap',
                    gap: '1rem'
                }}>
                    {sensors.map(sensor => (
                        <div
                            key={sensor.key}
                            style={{
                                background: 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)',
                                backdropFilter: 'blur(10px)',
                                border: '1px solid rgba(255,255,255,0.18)',
                                borderRadius: '20px',
                                padding: '1.5rem',
                                flex: '1 1 400px',
                                minHeight: '250px'
                            }}
                        >
                            <h3 style={{ 
                                color: '#fff', 
                                marginBottom: '1rem',
                                fontSize: '1rem',
                                fontWeight: '600',
                                letterSpacing: '0.5px'
                            }}>
                                {sensor.icon} {sensor.label} ({sensor.unit})
                            </h3>
                            <Graph data={history} dataKey={sensor.key} label={`${sensor.label} (${sensor.unit})`} />
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
